    #[test]
    unconstrained fn test_large_field_values_unconstrained() {
        let large_field = 0xffffffffffffffff;

        let bits: [u1; 64] = large_field.to_le_bits();
        assert_eq(bits[0], 1);

        let bytes: [u8; 8] = large_field.to_le_bytes();
